#!/usr/bin/python3import nmapimport urllib.requestimport urllib.errorimport zipfile#url for exploit db .zip fileurl = 'https://github.com/offensive-security/exploit-database/archive/master.zip'print("\nRetrieving Exploit Database- this may take a few moments\n")#destination pathurllib.request.urlretrieve(url, "/root/CYB333/exploit-database-master.zip")f = urllib.request.urlopen(url)data = f.read()with open("exploit-database-master.zip", "wb") as code:    code.write(data)fuk = zipfile.ZipFile('/root/CYB333/exploit-database-master.zip')fuk.extractall('/root/CYB333')fuk.close()print('Complete\n')print('Retrieving CVEs\n')# url to pull cveurl = 'https://cve.mitre.org/data/downloads/allitems-cvrf-year-2018.xml'# destination pathurllib.request.urlretrieve(url, '/root/CYB333/CVE.doc')print('Complete.\n')# PortScanner Moduleprint('Scan a system to check for open ports.\n')ns = nmap.PortScanner()# User inputs for IP and ports to be scannedusr_host = input("Please enter the IP of the host you want to scan: \n")usr_port = input("Please enter the port or range of ports you want to scan: \n")# Begin scan bannerprint("Scanning started. Please wait...\n")# Scanner initiationns.scan(usr_host, usr_port, '-sS -oN /root/CYB333/scan.txt')# End of programprint("Scanning complete! Results have been placed in scan.txt")